#!/bin/bash

LC_ALL='C'; unset LANGUAGE; echo;echo;echo -n 'System: ';uname -a; echo -n 'Uptime:';uptime; echo -n 'Release: '; sh -c 'cat /etc/*release 2>/dev/null|head -n 1';if [ -f /etc/debian_version ];then echo -n 'Debian-';cat /etc/debian_version;else sh -c 'cat /etc/*version 2>/dev/null|head -n 1';fi;echo -n 'IPs: ';ifconfig|grep 'inet addr'|grep -v ':127.'|awk -F : '{print $2}'|awk '{print $1}'|perl -p -i -X -e 's/[\n ]/, /g'|perl -p -i -X -e 's/, $//';echo;echo -n 'Users: ';grep -E '/bin/.*sh' /etc/passwd|wc -l|awk '{print $1}';echo -n 'Processes: ';ps aux | grep -v \\[ | wc -l |awk '{print $1}'; echo -n 'CPU Cores: ';grep processor /proc/cpuinfo|wc -l|awk '{print $1}';echo -n 'CPU Model:';cat /proc/cpuinfo|grep 'model name'|head -n 1|awk -F : '{print $2}';echo -n 'CPU Speed:';cat /proc/cpuinfo|grep 'cpu MHz' | head -n 1 | awk -F : '{print $2 " Mhz"}';echo -n 'Memory: ';cat /proc/meminfo|grep 'MemTotal'|awk '{print $2 " " $3}';echo -n 'HDD: ';df -h /|grep '/'|awk '{print $2}';if [ -f /usr/local/cpanel/version ];then echo -n 'CPanel: ';cat /usr/local/cpanel/version;echo;fi;if [ -f /usr/local/psa/version ];then echo -n 'Plesk: ';cat /usr/local/psa/version;fi;if [ -x /usr/local/directadmin/directadmin ];then echo -n 'Direct Admin ';/usr/local/directadmin/directadmin version;fi;
